import React from 'react'
import {useForm} from 'react-hook-form'

function Registration()
{
    const {register,handleSubmit,formState:{errors}}=useForm()

    //form submission
    const onFormSubmit=(userData)=>{
        console.log(userData)
    }
    return(
        <div className='row mt-3' >
            <p className="display-1 text-success text-center">User Registration</p>
            <div className="col-11 col-sm-8 col-md-6 mx-auto mt-3">
            <form onSubmit={handleSubmit(onFormSubmit)} className='w-50 mx-auto'>
                {/*username*/}
                <div className="mb-3">
                    <label htmlFor='un'>Username</label>
                    <input type="text"  id="" className="form-control" {...register("username")} />
                    
                </div>
                {/*email*/}
                <div className="mb-3">
                    <label htmlFor='email'>Email</label>
                    <input type="email"  id="email" className="form-control" {...register("email")} />
                    
                </div>
                <div className="mb-3">
                    <label htmlFor='dob'>Date of birth</label>
                    <input type="date"  id="dob" className="form-control" {...register("username")} />
                    
                </div>
                <button type="submit" className="btn btn-success"></button>


            </form>
            </div>
        
        </div>
    )
}
export default Registration
